		j = 1;
		while (j < n){
			f = j;
			while ((j < n) && (mas[j][i] >= mas[j - 1][i])){
				j++;
			}
			if (arr[f] < j){
				arr[f] = j;
			}
			j++;
		}
	}
	for (int i = 2; i < n + 1; i++){
