#include <stdio.h>
#include <stdlib.h>
#include <inttypes.h>

int compare(const void *a, const void *b) {
    return ( *(long *)a - *(long *)b);
}

int main() {
	int t, n, dup, max_dup;
	long a[20001];
	scanf("%d", &t);

	while (t--) {
		scanf("%d", &n);
		for (int i = 0; i < n; i++)
			scanf("%ld", &(a[i]));

		qsort(a, n, sizeof(long), compare);
		max_dup = 0;
		for (int i = 1; i < n; i++) {
			if (a[i] == a[i - 1]) {
				dup = 1;
				for (; (i < n) && (a[i] == a[i - 1]); i++, dup++);
				max_dup = (max_dup < dup) ? dup : max_dup;
			}
		}

		max_dup -= n - max_dup;
		if (max_dup < (n & 1))
			max_dup = (n & 1);
		printf("%d\n", max_dup);
	}
}

