#include<stdio.h>
#include<stdlib.h>
#include<inttypes.h>
int n,k,A,B;
int P[100000+1];
int lb(int x) {
	for(int i=0; i<k; i++) {
		if(P[i]>=x) return i;
	}
	return -1;
}
int rb(int x) {
	for(int i=k-1; i>=0; i--) {
		if(P[i]<=x) return i;
	}
	return -1;
}
int64_t dnq(int x,int l) {
	int64_t pa,pb,pc;
	int lx=lb(x);
	int rx=rb(x+l-1);
	if(lx>=0&&rx>=0&&lx<=rx) pc = (int64_t)B*(rx-lx+1)*l;
	else return A;
	if(l>1) {
		pa = dnq(x,l/2);
		pb = dnq(x+l/2,l/2);
		if(pc>pa+pb) pc=pa+pb;
	}
	return pc;
}
int cmp(const void *a, const void *b) {
	return *((int*)a)-*((int*)b);
}
int main() {
	scanf("%d %d %d %d", &n,&k,&A,&B);
	for(int i=0; i<k; i++) {
		int x;
		scanf("%d", &x);
		P[i]=x-1;
	}
	qsort(P, k, sizeof(int), cmp);
	printf("%" PRId64 "\n", dnq(0,1<<n));
}

