int main()
{
	int n;
	int s, v, l, r;
	scanf("%d", &n);
	int counts;
	while(n--){
		scanf("%d%d%d%d", &s, &v, &l, &r);

		counts = s / v;

		int left = l / v;
		if(l % v) left++;
		int right = r / v;
		if(r % v) right--;

		counts -= (right - left + 1);

		printf("%d\n", counts);
	}

	return 0;
}

