#include <stdio.h>
#include <stdlib.h>

#define N 100000

int compare(const void *a, const void *b) {
	int ia = *(int *) a;
	int ib = *(int *) b;

	return ia - ib;
}

int f(int n) {
	return n == 0 ? 0 : f(n / 2) + n % 2;
}

int main() {
	static int ff[N];
	int n, i, j, k;
	long long ans;

	scanf("%d", &n);
	for (i = 0; i < n; i++) {
		int a;

		scanf("%d", &a);
		ff[i] = f(a);
	}
	qsort(ff, n, sizeof *ff, compare);
	ans = 0;
	for (i = 0; i < n; ) {
		j = i + 1;
		while (j < n && ff[j] == ff[i])
			j++;
		k = j - i;
		ans += k * (k - 1) / 2;
		i = j;
	}
	printf("%lld\n", ans);
	return 0;
}

