/* Permutation Sort
 *
 * Educational Codeforces Round 109 B
 * https://codeforces.com/contest/1525/problem/B
 */

#include <stdio.h>
#include <stdlib.h>

void solve(void)
{
    int y = 1;
    int sorted = 1;
    int arrSize;
    scanf("%d", &arrSize);
    int *arr = malloc(sizeof(int) * arrSize);
    for (int i = 0; i < arrSize; ++i) {
        scanf("%d", &arr[i]);
        if (arr[i] != y) {
            sorted = 0;
        }
        ++y;
    }

    if (sorted == 1) {
        printf("%d\n", 0);

    } else if (arr[0] == 1) {
        printf("%d\n", 1);
    } else if (arr[arrSize - 1] == 1) {
        printf("%d\n", 1);
    } else {
        printf("%d\n", 2);
    }

    free(arr);
}

int main(void)
{
    int numberOfTestCases;
    scanf("%d", &numberOfTestCases);

    for (int i = 0; i < numberOfTestCases; ++i)
        solve();
    return EXIT_SUCCESS;
}

