#include <string.h>

#define N	5000

int main() {
	int t;

	scanf("%d", &t);
	while (t--) {
		static int pp[N], kk[N][N];
		int n, i, j;
		long long ans;

		scanf("%d", &n);
		for (i = 0; i < n; i++)
			memset(kk[i], 0, n * sizeof *kk);
		for (i = 0; i < n; i++) {
			scanf("%d", &pp[i]), pp[i]--;
			kk[i][pp[i]] = 1;
		}
		for (i = 0; i < n; i++)
			for (j = 1; j < n; j++)
				kk[i][j] += kk[i][j - 1];
		for (j = 0; j < n; j++)
			for (i = 1; i < n; i++)
				kk[i][j] += kk[i - 1][j];
		ans = 0;
		for (i = 1; i < n; i++)
			for (j = i + 1; j + 1 < n; j++)
				ans += kk[i - 1][pp[j]] * (kk[n - 1][pp[i]] - kk[j][pp[i]]);
		printf("%lld\n", ans);
	}
	return 0;
}

