#include <stdio.h>

#define DIV_CEIL(a, b) ((a) / (b) + ((a) % (b) != 0))
#define MAX(a, b) ((a) > (b) ? (a) : (b))

static long long as[10000];

int main(void) {
	long long t, A, B, n, b, i, count, max;

	scanf("%lld", &t);

	while (t--) {
		max = 0;

		scanf("%lld %lld %lld", &A, &B, &n);

		for (i = 0; i < n; ++i) {
			scanf("%lld", as + i);
		}

		for (i = 0; i < n; ++i) {
			scanf("%lld", &b);

			count = DIV_CEIL(b, A);
			max = MAX(max, as[i]);
			B -= count * as[i];
		}

		puts(B + max >= 1 ? "YES" : "NO");
	}
}

