int dfs(int v, int *intr, int* c, int m){
    int i;
    for(i=0; i<m; i++){
        if(intr[v*m + i] == 1){
            if(c[i] == 0){
                c[i] = c[v]%2 + 1;
                dfs(i, intr, c, m);
            }
            else if(c[i] == c[v])
                return 0;
        }
    }
    return 1;
}

