	scanf("%d %d", &n, &m);
	int intr[m][m], v[2][m], c[m];;
	for(i=0; i<m; i++){
		scanf("%d %d", &x, &y);
		if(x > y){
			a = x;
			x = y;
			y = a;
		}

		v[0][i] = --x;
		v[1][i] = --y;
	}


