			||(v[0][i] < v[0][j] && v[1][i] > v[0][j] && v[1][i] < v[1][j])){
				intr[i][j] = 1;
				intr[j][i] = 1;
			}
		}
	}

	for(i=0; i<m; i++){
		if(!c[i]){
			c[i] = 1;
