int main() {
	prefix[0] = 0;
	totheRight[0] = 0;
	scanf("%d", &n);
	for (i = 1; i <= n; i++) {
		scanf("%d", &tmp);
		prefix[i] = tmp + prefix[i - 1];
		totheRight[i] = 0;
	}

	long long sum = prefix[n];
	int ans = 0;

	if (n < 3 || sum % 3 != 0) {
		printf("0\n");
		return ans;
	}
	long long partSum = sum / 3;

	int k = 0;
	for (j = n - 1; j >= 2; j--) {
		if (prefix[j] == partSum * 2)k++;
		totheRight[j] = k;
	}
	for (i =n-2; i >= 1; i--) {
		if (prefix[i] == partSum )
		ans += totheRight[i+1];
	}
	printf("%d", ans);
	return 0;
}

