#include<stdio.h>

void rot(char str[], int l, int r, int k)
{
    int i;
    l--;
    r--;
    char temp[10000];

    if(strlen(str)==1)
        return;

    strcpy(temp, str);
    k%=r-l+1;
    for(i=0; i<=r-l; i++)
    {
        str[l + ((i+k)%(r-l+1))]=temp[l+i];
    }
}

