	int n,i,mayor = 1, cont = 1;
	scanf("%d", &n);

	for (i = 0; i < n; i++) {
		scanf("%d %d", &v[i].l, &v[i].r); 
	}

	quickSort(v, 0, n-1);

	mayor = v[0].r;
	for (i = 1; i < n; i++) {
		if (v[i].l > mayor) {
			mayor = v[i].r;
			++cont;
		}
	}

	printf("%d\n", cont);

	/*for (i = 0; i < n; i++) {
		printf("%d %d\n", v[i].l, v[i].r); 
	}
	printf("\n");*/

	return 0;
}

int comparar (const alq *izq, const alq *der) {
	int i = (der -> r - izq -> r);
	return (i ? i : (der -> l - izq -> l));
}

void quickSort(alq v[], int primero, int ultimo) {

	int i, j, central;
	alq p;

	central = (primero + ultimo) / 2;
	p = v[central];
	i = primero;
	j = ultimo;

	do {
		while (comparar(v+i, &p) > 0) i++;
		while (comparar(v+j, &p) < 0) j--;

		if (i <= j) {
			alq tmp;
			tmp = v[i];
			v[i] = v[j];
			v[j] = tmp;
			i++;
			j--;
		}
	} while (i <= j);

	if (primero < j)
		quickSort(v, primero, j);
	if (i < ultimo)
		quickSort(v, i, ultimo);
}

