			{

				t = a[i];
				a[i] = a[i + 1];
				a[i + 1] = t;
			}

	//count coins
	sum = 0;
	for (i = n-1; i >= 0; --i)
	{
		if (sum > fair_share)
			break;
		sum += a[i];
		counter++;
	}

	printf("%d\n", counter);


	return 0;
}

