int req[3];
int max(int x,int y)
{
	return x>y?x:y;
}
int main()
{
	//freopen("program.txt","r",stdin);
	int turns,n,max_len=0,max_pos;
	scanf("%d",&turns);
	int i,j,k;
	for(i=0;i<3;i++)
	{
		scanf("%s",str[i]);
	}
