	for(i=0;i<3;i++)
	{
		memset(hash,0,sizeof(hash));
		for(j=0;j<n;j++)
		{
			hash[str[i][j]]++;
			req[i]=max(req[i],hash[str[i][j]]);
		}
		req[i]=n-req[i];
	}
	//	now we will calculate maximum a string can get
