/* upsolve with Dukkha */
#include <limits.h>
#include <stdio.h>

int aa[16];

long long count(long long y, int n) {
	long long cnt = y;
	int a, p, b, i;

	for (b = 1; b < 1 << n; b++) {
		a = 1, p = 0;
		for (i = 0; i < n; i++)
			if ((b & 1 << i) > 0) {
				a *= aa[i];
				p = 1 - p;
			}
		if (p == 0)
			cnt += y / a;
		else
			cnt -= y / a;
	}
	return cnt;
}

int main() {
	int t;

	scanf("%d", &t);
	while (t-- > 0) {
		int x, p, k, n, a;
		long long lower, upper;

		scanf("%d%d%d", &x, &p, &k);
		n = 0;
		for (a = 2; a * a <= p; a++)
			if (p % a == 0) {
				aa[n++] = a;
				while (p % a == 0)
					p /= a;
			}
		if (p > 1)
			aa[n++] = p;
		lower = 0, upper = LLONG_MAX;
		while (upper - lower > 1) {
			long long y = (lower + upper) / 2;
			long long cnt = count(y, n) - count(x, n);

			if (cnt < k)
				lower = y;
			else
				upper = y;
		}
		printf("%lld\n", upper);
	}
	return 0;
}

