#include <stdio.h>
#include <string.h>

void my_strrev(char *p)
{
	char *q = p;
	while (q && *q) ++q;
	for (--q; p < q; ++p, --q)
		*p = *p ^ *q,
		*q = *p ^ *q,
		*p = *p ^ *q;
}

int main(int argc, char** argv)
{
	int t;
	char buf[16], col_buf[16];
	char digits[] = "0123456789";
	char letters[] = "AABCDEFGHIJKLMNOPQRSTUVWXYZ";
	int powers26[] = { 1, 26, 676, 17576, 456976, 11881376 };
	size_t idx;
	int row, col;

	scanf("%d", &t);

	while (t--)
	{
		scanf("%s", buf);

		idx = strcspn(buf, digits);

		if (idx == 1 && buf[idx - 1] == 'R')
		{
			if (sscanf(buf, "R%dC%d", &row, &col) != 2)
				goto A1;
			else
				goto R1C1;
		}
		else
			goto A1;

	R1C1:
		idx = 0;
		while (col != 0)
		{
			col_buf[idx] = letters[(col % 26)];
			col /= 26;
			idx++;
		}
		col_buf[idx] = '\0';
		my_strrev(col_buf);

		printf("%s%d\n", col_buf, row);
		continue;
	A1:
		strncpy(col_buf, buf, idx);
		col_buf[idx] = '\0';
		sscanf(buf + idx, "%d", &row);
		col = 0;

		for (idx = 0; idx < strlen(col_buf); idx++)
			col += (col_buf[idx] - 64) * powers26[strlen(col_buf) - idx - 1];

		printf("R%dC%d\n", row, col);


		continue;
	}
	return 0;
}

