#include<string.h>
#include<math.h>
#define N 1000001

int main()
{
    int n,k;
    scanf("%d %d",&n,&k);
    // int arr[N];
    int i=0;
    if((n/k)<3)
    {
        printf("%d\n",-1);
        return 0;
    }
    while(i<n)
    {
        int count = 0;
        while(count<2)
        {
            int end = i+2;
            for(int start=i;start<end;start++,i++)
            {
                if(i>=n)
                {
                    printf("\n");
                    return 0;
                }
                int num;
                num = (k+i-count)%k + 1;
                printf("%d ",num);
                // printf("%d\n",i);
            }
            count++;
        }
    }
}

