    number *k2 = (number *)b;
    if(k1->val - k2->val <0)
        return 1;
    else if(k1->val - k2->val ==0)
    {
        if(k1->val2 - k2->val2 < 0 )
            return -1;
        else
            return 1;
    }
    else
        return -1;
}
