		for (i = 1; i < n; i++)
			if (aa[i] > aa[i - 1])
				aa[i] = aa[i - 1];
		for (i = n - 2; i >= 0; i--)
			if (bb[i] > bb[i + 1])
				bb[i] = bb[i + 1];
		max = -1000000001;
		for (i = 0; i < n; i++) {
