void bubblesort(long matriz[][2], long n)
{
	long i, j, aux1, aux2;
	for (i = 0; i < n; ++i)
	{
		for (j = 0; j < n-1-i; ++j)
		{
			if (matriz[j][str]> matriz[j+1][str])
			{
				aux1 = matriz[j+1][str];
				aux2 = matriz[j+1][bonus];
				matriz[j+1][str] = matriz[j][str];
				matriz[j+1][bonus] = matriz[j][bonus];
				matriz[j][str] = aux1;
				matriz[j][bonus] = aux2;
			}
		}
	}
}

int main(int argc, char const *argv[])
{
	long kirito_s, n, aux1, aux2;
	long matriz[n][2];
	scanf("%ld %ld", &kirito_s, &n);
	for (long i = 0; i < n; ++i)
	{
		scanf("%ld %ld", &aux1, &aux2);
		matriz[i][str] = aux1;
		matriz[i][bonus] = aux2;
	}
	bubblesort(matriz, n);

	for (long i = 0; i < n; ++i)
	{
		if (kirito_s > matriz[i][str])
		{
			kirito_s += matriz[i][bonus];
			if (i == n-1)
			{
				printf("YES\n");
				return 0;
			}
		}
		else
		{
			printf("NO\n");
			return 0;
		}
	}
}
