			if(s[i][0]==s[p][or]) 
				ret+=arr(!0, i, l-s[i][0]);
			if(s[i][1]==s[p][or] && s[i][1]!=s[i][0])
				ret+=arr(!1, i, l-s[i][1]);
		}
	}
	return k[or][p][l]=1, c[or][p][l]=ret%M;
}

