	for(i=0;i<n;++i) ret=(ret+arr(0, i, l-s[i][!0]))%M;
	for(i=0;i<n;++i) if(s[i][1]!=s[i][0]) 
		ret=(ret+arr(1, i, l-s[i][!1]))%M;
