{
    int temp,i;
    if(n==m) return n;

    else {
        if(n<m) {
            temp=n;
            n=m;
            m=temp;
        }

        if(n%m==0) return n;
        else {
            for(i=2;i<=m;i++){
                if(n*i%m==0) return n*i;
            }
        }
    }
