int memo[5001][5001];
int min(int a,int b)
{
    if(a<b)
    return a;
    else
    return b;
}
int get(int a,int b)
{
    if(a==b)
    return 0;
    while(count[a]==0&&a<b)
    a++;
    while(count[b]==0)
    b--;
    if(a==b)
    return 0;
    if(memo[a][b]!=0)
    return memo[a][b];
    //printf("count[%d]=%d count[%d]=%d\n",a,count[a],b,count[b]);
    if(b<=(2*a))
    return 0;
    return memo[a][b]=min(count[a]+get(a+1,b),count[b]+get(a,b-1));
}
