#include <stdio.h>
#include <string.h>
#include <math.h>
#include <stdlib.h>

int main() {
    int tam=0,nro,mult=0,add=0;
    char at,corda[100];
    while(scanf(" %c", &at)!=EOF){
        if(at=='+'){
            mult++;
        }
        if(at=='-'){
            mult--;
        }
        if(at==':'){
            scanf("%[^\n]s", corda);
            tam=strlen(corda);
            nro=mult*tam;
            add=add+nro;
        }
    }
    printf("%d\n", add);
    return 0;
}
