#include <stdio.h>
#include <stdlib.h>

#define MAX 100000

typedef struct
{
	unsigned int hist;
	unsigned int min;
	unsigned int max;
} hist_t;

unsigned int N;
unsigned int i;
hist_t a[MAX];
unsigned int ai;
unsigned int dMax;
unsigned int aMin;
unsigned int aMax;

int cmp(const void *a, const void *b)
{
	const hist_t aa = *(hist_t*)a;
	const hist_t bb = *(hist_t*)b;

	return bb.hist - aa.hist;
}

int main()
{
//	freopen("input.txt", "r", stdin);
	scanf("%d", &N);

	for ( i = 0; i < N; i++ )
	{
		scanf("%d", &ai);
		a[ai].hist++;
		if ( 0 == a[ai].min )
		{
			a[ai].min = a[ai].max = i + 1;
		}
		else
		{
			a[ai].max = i + 1;
		}
	}

	qsort(a, MAX, sizeof(hist_t), cmp);

	dMax = a[0].max - a[0].min;
	aMin = a[0].min;
	aMax = a[0].max;
	i = 1;
	while ( ( a[0].hist == a[i].hist ) && ( i < N ) )
	{
		if ( a[i].max - a[i].min < dMax)
		{
			dMax = a[i].max - a[i].min;
			aMin = a[i].min;
			aMax = a[i].max;
		}
		i++;
	}

	printf("%d %d\n", aMin, aMax);

	return 0;
}

