			}
			else if (stack[count - 1] == -2) stack[count++] = -2;
			else if (stack[count - 1] == -1) stack[count - 1] = 2;

			while (count > 1 && stack[count - 1] > 0){
				if (stack[count - 2] < 0) break;
				stack[count - 2] = stack[count - 2] + stack[count - 1];
				count--;
			}
		}
	}
	max = 0; pos = 1; k = 0;
	for (i = 0; i < count; i++){
		if (stack[i] > max){
			max = stack[i];
