#include<stdio.h>
int max(int a,int b){
    if(a>b)
        return a;
    else if(b>a)
        return b;
}
int main()
{
    int t;
    scanf("%d",&t);
    while(t--){
        int a,b,c,x,y,s,p;
        scanf("%d %d %d %d %d",&a,&b,&c,&x,&y);
        s=max(0,x-a);
        p=max(0,y-b);
        if((s+p)<=c)
            printf("Yes\n");
        else
            printf("No\n");
    }
    return 0;
}

