		if (strcmp(bb, "abc") == 0 && kk[0] > 0 && kk[1] > 0 && kk[2] > 0) {
			while (kk[0]--)
				aa[n++] = 'a';
			while (kk[2]--)
				aa[n++] = 'c';
			
		} else {
			while (kk[0]--)
				aa[n++] = 'a';
			while (kk[1]--)
				aa[n++] = 'b';
			while (kk[2]--)
				aa[n++] = 'c';
		}
		for (a = 3; a < A; a++)
			while (kk[a]--)
				aa[n++] = a + 'a';
		aa[n] = 0;
		printf("%s\n", aa);
	}
	return 0;
