#include <stdio.h>

int count_odds(int *array, int length){
  int result = 0;
  for(int i = 0; i < length; i++){
    if(array[i] % 2 != 0){
      result++;
    }
  }
  return result;
}

int find_min_odd(int *array, int length){
  int actual = 1000000001, result = -1;
  for(int i = 0; i < length; i++){
    if(array[i] % 2 != 0 && array[i] < actual){
      actual = array[i];
      result = i;
    }
  }
  return result;
}

__int64 sum_all_except(int *array, int length, __int64 position){
  __int64 result = 0;
  for(__int64 i = 0; i < length; i++){
    if(i != position){
      result += array[i];
    }
  }
  return result;
}

int main(){
  __int64 n, min_odd;
  scanf("%I64d", &n);
  __int64 array[n];
  for(int i = 0; i < n; i++){
    scanf("%I64d", &array[i]);
  }
  int odd_amount = count_odds(array, n);
  if(odd_amount % 2 == 0){
    min_odd = -1;
  }
  else{
    min_odd = find_min_odd(array, n);
  }
  printf("%I64d\n", sum_all_except(array, n, min_odd));
  return 0;
}

