{
  double s, c, res;

  res = -1;

  if(b > 1){
    a /= b;
    s = b;
    b = 1;
  }
  else{
    s = 1;
    c = floor((a-b)/2)*2;
    if(c > 0) res = (b-a)/c;
  }
  c = floor((b+a)/2)*2;
  if(c > 0 && (res < 0 || s*(b+a)/c < res)) res = s*(b+a)/c;

  return res;
}

