int arr[100001];
int n;
int found(int key)
{
	int start=1,end=n;
	while(start<=end)
	{
		int mid=(start+end)/2;
		if(arr[mid]==key)
		 return 1;
		if(arr[mid]>key)
		 end=mid-1;
		else
		 start=mid+1;
	}
