            if (max == b && b > c && b > d)
                b++;
            if (max == c && c > b && c > d)
                c++;
            if (max == d && d > b && d > c)
                d++;
            printf("%ld %ld %ld\n", max - b + 1, max - c + 1, max - d + 1);
        }
    }
}

