	char word[n+1];
	scanf("%s" , word);

	if(n == p+q)
	{
		printf("2\n");
		for (int i = 0; i < p; ++i)
		{
			printf("%c", word[i] );
		}
		printf("\n");
		for (int i = p; i < n; ++i)
		{
			printf("%c", word[i] );
		}
		printf("\n");

		return 0;
	}
	else if(n%p != 0 && n%q != 0)
	{
		int z , i;
		for ( i = 0; i < n; ++i)
		{
			int x = n - (p*i);
			if(x%q == 0)
			{
				z = i;
				break;
			}
		}

		int y = (n-(z*p))/q;

		printf("%d\n",  z + y);

		for ( i = 0; i < z; ++i)
		{
			for (int j = 0; j < p; ++j)
			{
				int f = p*i+j;
				printf("%c",word[f]);
			}
			printf("\n");
		}

		for (i = 0; i < y; ++i)
		{
			int f = z*p;
			for (int j = 0; j < q; ++j) 
			{
				int g = f + q*i+j;
				printf("%c",word[g]);
			}
			printf("\n");
		}

		if(i == y)
			return 0;

		printf("-1\n");
		return 0;
	}
	else
	{
		int s;
		if(n % p == 0)
		{
			s = p;
		}
		else
		{
			s = q;
		}

		printf("%d\n", n/s );

		for (int i = 0; i < n/s; ++i)
		{
			for (int j = 0; j < s; ++j)
			{
				int x = i*s+j;
				printf("%c", word[x]);
			}
			printf("\n");
		}
	}
	return 0;
}

