    iloop = 0;

    scanf("%d", &tc);

    while(0 < tc--)
    {
        for(iloop = 0; iloop < 50; ++iloop)
            lut[iloop] = iloop + 1;
        iloop = 0;

        scanf("%d", &perm_len);
