        if(NULL == ptr)
        {
            return 1;
        }

        while(iloop++ < perm_len * 2)
        {
            scanf("%d", ptr);
            if(lut[*ptr] != 0)
            {
                printf("%d ", *ptr);
                lut[*ptr] = 0;
            }
            ++ptr;
        }

        printf("\n");
        free(ptr);
    }

    return 0;
}

