        count=0;
        scanf("%d %d", &a, &b);

        if(a > b){
            a=a+b;
            b= a-b;
            a= a-b;
        }

        while(1){
            if(a==b)
                break;
            if(b>=5){
                count += b/5;
                if(a*(count+1)-b==1){
                    count++;
                    b=0;
                }
                else
                    b = b%5;

            }
            if(b>=2){
                count += b/2;
                b = b%2;
                if(b==1){
                    count++;
                    b=0;
                }
            }
            if(b>=1){
                count += b/1;
                b=b%1;
            }
            if(b==0)
                break;
        }
        printf("%d\n", count);
    }

    return 0;
