		for (i = 1; i < n; i++)
			if (aa[i] != aa[i - 1]) {
				same = 0;
				break;
			}
		if (same) {
			printf("0\n");
			continue;
		}
		k_ = 0;
		for (i = 1; i < n; i++) {
			if (aa[i - 1] == aa[i])
				continue;
			i1 = i - 1, i2 = i, k = 2;
			while (k <= i && aa[i - k] == aa[i - 1])
				k++;
			while (aa[i1] + aa[n - 1] >= aa[i2] * 2) {
				lower = i2, upper = n - 1;
				while (upper - lower > 1) {
					i3 = (lower + upper) / 2;
					if (aa[i1] + aa[i3] >= aa[i2] * 2)
						upper = i3;
					else
						lower = i3;
				}
				i1 = i2, i2 = upper, k++;
			}
			k_ = max(k_, k);
		}
		printf("%d\n", n - k_);
	}
	return 0;
}

