	uint8_t t;
	scanf("%hhu\n", &t);

	for (uint8_t i = 0; i < t; ++i) {
		uint16_t n, a, b, h;
		uint16_t count = 0;
		char c;

		scanf("%hu %hu %hu %hu\n", &n, &a, &b, &h);
		while ((c = getchar()) != '\n' && c == '0') {
			++count;
		}

		if (b < a) {
			a, b = b, a;
			count = n - count;
		}

		printf("%lu\n", (unsigned long)count * (unsigned long)a + (unsigned long)(n - count) * min((unsigned long)h + (unsigned long)a, (unsigned long)b));
	}

