typedef long long ll;

#define repl(i,a,b) for(ll i=(ll)(a);i<(ll)(b);i++)
#define rep(i,n) repl(i,0,n)
#define mmax(x,y) (x>y?x:y)
#define mmin(x,y) (x<y?x:y)

ll n,p,m;
ll d[100010],t[100010];

int main(){
  scanf("%lld%lld%lld",&n,&p,&m);
  d[0]=1;
  d[n+1]=m;
  repl(i,1,n+1){
    scanf("%lld%lld",d+i,t+i);
  }
  ll res=0;
  ll crt=0;
  rep(i,n+1){
    ll dd=d[i+1]-d[i];
    ll lb=-1,ub=m+1;
    while(ub-lb>1){
      ll mid=(lb+ub)/2;
      if(crt-mid*p<0)ub=mid;
      else lb=mid;
    }
    ll j=ub-1;
    j=mmax(0LL,j);
    res+=mmax(0LL,dd-j);
    crt+=-p*dd+t[i+1];
  }
  if(crt<0)res++;
  printf("%lld\n", res);
  return 0;
}

