int sort(int a[],int n){
	int bound,exchange,x,count=0;
	exchange=n;
	while(exchange!=0){
		bound=exchange;exchange=0;
		for(int i=1;i<bound;i++){
			if(a[i-1]>a[i]){
				x=a[i-1];
				a[i-1]=a[i];
				a[i]=x;
				exchange=i;
				count++;
			}

		}
	}
	return count;

}

