#include <stdio.h>

#define N	200000
#define MD	1000000007
#define INV2	((MD + 1) / 2)

int d_, x_, y_;

void gcd_(int a, int b) {
	if (b == 0)
		d_ = a, x_ = 1, y_ = 0;
	else {
		int tmp;

		gcd_(b, a % b);
		tmp = x_ - a / b * y_, x_ = y_, y_ = tmp;
	}
}

int inv(int a) {
	gcd_(a, MD);
	return x_;
}

int ff[N + 1], gg[N + 1];

void init() {
	int i, f;

	f = 1;
	for (i = 0; i <= N; i++) {
		gg[i] = inv(ff[i] = f);
		f = (long long) f * (i + 1) % MD;
	}
}

int choose(int n, int k) {
	return k < 0 || k > n ? 0 : (long long) ff[n] * gg[k] % MD * gg[n - k] % MD;
}

int main() {
	int n, k, ans;
	long long s, x, p;

	init();
	scanf("%d%lld", &n, &s);
	ans = 0, x = 1, p = 1;
	for (k = 1; k <= n; k++) {
		int t;
		long long s_;

		scanf("%d", &t);
		if ((s_ = s - t) < 0)
			break;
		x = (x * 2 - choose(k - 1, s)) % MD;
		if (s_ < k) {
			if (s > k)
				s = k;
			while (s > s_)
				x = (x - choose(k, s--)) % MD;
		} else
			s = s_;
		ans = (ans + x * (p = p * INV2 % MD)) % MD;
	}
	if (ans < 0)
		ans += MD;
	printf("%d\n", ans);
	return 0;
}

