	if (a < b) {
		
		return a;
	}
	else {
		
		return b;
	}
}

int64_t myAbs(int64_t a, int64_t b) {
	
	if (a > b) {
		
		return a - b;
	}
	else {
		
		return b - a;
	}
}

void solve() {
	
	int64_t a, b, c, m;
	
	scanf("%"SCNd64"%"SCNd64, &a, &b);
	
	if (a == b) {
		
		printf("0 0\n");
	}
	else {
		
		c = myAbs(a, b);
		m = min(a % c, c - a % c);
	
		printf("%"PRId64" %"PRId64"\n", c, m); 
	}
}


