#include<stdio.h>
int prime(int n)
{
	int i;
	for (i = 2; i * i <= n; i++)
		if (n % i == 0)
			return i;
	return n;
}
int main()
{
	int t;
	scanf("%d", &t);
	int n;
	int i, j;
	int d[100005], dd[100005], mm;
	int m;
	for (; t > 0; t--)
	{
		scanf("%d", &n);
		m = 0;
		for (i = 2; i * i <= n; i++)
		{
			if (n % i == 0)
			{
				d[m] = i;
				m++;
			}
		}
		i--;
		for (; i > 1; i--)
		{
			if (i * i == n)
				continue;
			if (n % i == 0)
			{
				d[m] = n / i;
				m++;
			}
		}
		int p[1003], pp = 0;
		int v = n;
		while (v > 1)
		{
			p[pp] = prime(v);
			while (v % p[pp] == 0)
				v /= p[pp];
			pp++;
		}
		printf("%d", n);
		for (i = 0;; i++)
		{
			printf(" %d", p[i]);
			if (i == pp - 1)
				break;
			mm = 0;
			for (j = 0; j < m; j++)
			{
				if (d[j] % p[i] > 0)
				{
					dd[mm] = d[j];
					mm++;
				}
				else if (d[j] != p[i] * p[i + 1] && d[j] != p[i])
					printf(" %d", d[j]);
			}
			if (p[i] * p[i + 1] < n)
				printf(" %d", p[i] * p[i + 1]);
			for (j = 0; j < mm; j++)
				d[j] = dd[j];
			m = mm;
		}
		printf("\n");
		if (pp == 2 && p[0] * p[1] == n)
			printf("1\n");
		else
			printf("0\n");
	}
	return 0;
}

