#include <stdio.h>
#include <string.h>

#define N	1000000

int power(int k) {
	return k == 0 ? 1 : (int) (power(k - 1) * 26LL % 1000000007LL);
}

int main() {
	static char pp[N + 1];
	static int yy[N + 1], zz[N];
	int n, p, m, i, j, l, r, k;

	scanf("%d%d", &n, &m);
	scanf("%s", pp);
	for (j = 0; j < m; j++) {
		scanf("%d", &yy[j]);
		yy[j]--;
	}
	yy[m] = n;
	p = strlen(pp);
	for (i = 1, l = r = 0; i < p; i++)
		if (i < r && zz[i - l] < r - i)
			zz[i] = zz[i - l];
		else {
			l = i;
			if (r < i)
				r = i;
			while (r < p && pp[r] == pp[r - l])
				r++;
			zz[i] = r - l;
		}
	k = 0;
	for (j = 1; j <= m; j++) {
		if (yy[j - 1] + p > yy[j]
				&& zz[yy[j] - yy[j - 1]] != yy[j - 1] + p - yy[j]) {
			printf("0\n");
			return 0;
		}
		if (yy[j] > yy[j - 1] + p)
			k += yy[j] - yy[j - 1] - p;
	}
	printf("%d\n", power(k));
	return 0;
}

