		puts("0");
		return EXIT_SUCCESS;
	}

	qsort(str, len, 1, cmp);

	for (len = 0, p = str; sum < target; len++)
		sum += 9 - *p;

	printf("%d\n", (int)len);
	return EXIT_SUCCESS;
}

