#include <stdio.h>

int n;
int arr[100];
int uniqArr[100];
int freq[100];
int len = 0;
int uniq[100] = {0};

int in(int a)
{
	for(int i = 0; i < len; i++)
	{
		if(uniqArr[i] == a)
		{
			return 1;
		}
	}
	return 0;
}
int inA(int a)
{
	for(int i = 0; i < len; i++)
	{
		if(arr[i] == a)
		{
			return 1;
		}
	}
	return 0;
}
void buildFreq()
{
	for(int i = 0; i < n; i++)
	{
		if(!(in(arr[i])))
		{
			int f = 0;
			for(int j = i; j<n; j++)
			{
				if(arr[j] == arr[i])
				{
					f++;
				}
			}
			freq[len] = f;
			uniqArr[len] = arr[i];
			len++;
		}
	}
}
void buildUniq()
{
	for(int i = 0; i < len; i++)
	{
		if(inA(-1*uniqArr[i]))
		{
			uniq[i] = 1;
		}
	}
}
void solve()
{
	buildFreq();
	buildUniq();
	int count = 0;
	for(int i = 0; i < len; i++)
	{
		if((freq[i]>1) && (!(uniq[i])))
		{
			count++;
		}
	}
	printf("%d\n", count+len);
	len = 0;
}

